resources.document:
query SearchNote(
  $query: String!
  $resources: [SearchResourceKind!]
  $coediting: Boolean
  $updated: SearchDate
  $groupIds: [ID!]
  $userIds: [ID!]
  $folderIds: [ID!]
  $likerIds: [ID!]
  $isArchived: Boolean
  $sortBy: SearchSortKind
  $first: Int!
) {
  search(
    query: $query
    resources: $resources
    coediting: $coediting
    updated: $updated
    groupIds: $groupIds
    userIds: $userIds
    folderIds: $folderIds
    likerIds: $likerIds
    isArchived: $isArchived
    sortBy: $sortBy
    first: $first
  ) {
    edges {
      node {
        document {
          ... on Node {
            id
          }
        }
        title
        url
        contentSummaryHtml
        path
        author {
          account
          realName
        }
      }
    }
  }
}

query SearchFolder($query: String!, $first: Int!) {
  searchFolder(query: $query, first: $first) {
    edges {
      node {
        name
        fixedPath
        group {
          name
          isPrivate
        }
      }
    }
  }
}

query GetGroups($first: Int!) {
  groups(first: $first) {
    edges {
      node {
        id
        name
        isDefault
        isArchived
      }
    }
  }
}

query GetFolders($first: Int!) {
  folders(first: $first) {
    edges {
      node {
        id
        name
      }
    }
  }
}

query GetNotes($folderId: ID!, $first: Int!, $last: Int) {
  notes(folderId: $folderId, first: $first, last: $last) {
    edges {
      node {
        id
        title
        url
      }
    }
  }
}

query GetNote($id: ID!) {
  note(id: $id) {
    id
    title
    content
  }
}

query GetNoteFromPath($path: String!, $first: Int!) {
  noteFromPath(path: $path) {
    id
    title
    content
    url
    author {
      account
      realName
    }
    folders(first: $first) {
      edges {
        node {
          id
          name
          fullName
          fixedPath
          group {
            id
            name
          }
        }
      }
    }
    comments(first: $first) {
      edges {
        node {
          id
          anchor
          content
          author {
            account
            realName
          }
          replies(first: $first) {
            edges {
              node {
                id
                anchor
                content
                author {
                  account
                  realName
                }
              }
            }
          }
        }
      }
    }
    inlineComments(first: $first) {
      edges {
        node {
          id
          anchor
          content
          author {
            account
            realName
          }
          replies(first: $first) {
            edges {
              node {
                id
                anchor
                content
                author {
                  account
                  realName
                }
              }
            }
          }
        }
      }
    }
  }
}

query GetFolder($id: ID!, $first: Int!) {
  folder(id: $id) {
    name
    fullName
    fixedPath
    createdAt
    updatedAt
    group {
      id
      name
    }
    folders(first: $first) {
      edges {
        node {
          id
          name
        }
      }
    }
    notes(first: $first) {
      edges {
        node {
          id
          title
        }
      }
    }
  }
}

query GetFolderFromPath($path: String!, $first: Int!) {
  folderFromPath(path: $path) {
    name
    fullName
    fixedPath
    createdAt
    updatedAt
    group {
      id
      name
    }
    folders(first: $first) {
      edges {
        node {
          id
          name
        }
      }
    }
    notes(first: $first) {
      edges {
        node {
          id
          title
        }
      }
    }
  }
}

query GetFeedSections($kind: FeedKind!, $groupId: ID!, $first: Int!) {
  feedSections(kind: $kind, groupId: $groupId, first: $first) {
    edges {
      node {
        ... on FeedNote {
          date
          note {
            id
            title
            contentSummaryHtml
          }
        }
        ... on FeedFolderParcel {
          date
          folder {
            id
            name
          }
          notes(first: $first) {
            edges {
              node {
                id
                title
                contentSummaryHtml
              }
            }
          }
        }
        ... on FeedUserParcel {
          date
          user {
            account
            realName
          }
          notes(first: $first) {
            edges {
              node {
                id
                title
                contentSummaryHtml
              }
            }
          }
        }
      }
    }
  }
}

mutation CreateNote($input: CreateNoteInput!) {
  createNote(input: $input) {
    clientMutationId
    note {
      id
      title
      content
    }
  }
}

mutation CreateComment($input: CreateCommentInput!) {
  createComment(input: $input) {
    comment {
      id
    }
  }
}

mutation CreateCommentReply($input: CreateCommentReplyInput!) {
  createCommentReply(input: $input) {
    reply {
      id
    }
  }
}

mutation CreateFolder($input: CreateFolderInput!) {
  createFolder(input: $input) {
    folder {
      id
    }
  }
}

mutation MoveNoteToAnotherFolder($input: MoveNoteToAnotherFolderInput!) {
  moveNoteToAnotherFolder(input: $input) {
    note {
      id
    }
  }
}

mutation AttachNoteToFolder($input: AttachNoteToFolderInput!) {
  attachNoteToFolder(input: $input) {
    note {
      id
    }
  }
}

mutation UpdateNoteContent($input: UpdateNoteContentInput!) {
  updateNoteContent(input: $input) {
    note {
      id
      title
      content
    }
  }
}
